mapper.select:
SELECT COUNT(*) as cnt FROM news
WHERE classifyno=#{classifyno}
WHERE classifyno=#{classifyno} AND (UPPER(title) LIKE '%' || UPPER(#{word}) || '%' OR UPPER(content) LIKE '%' || UPPER(#{word}) || '%' OR UPPER(word) LIKE '%' || UPPER(#{word}) || '%')

SELECT newsno, memberno, classifyno, title, content, recom, cnt, replycnt, rdate, file1, file1saved, thumb1, size1, map, youtube, r FROM ( SELECT newsno, memberno, classifyno, title, content, recom, cnt, replycnt, rdate, file1, file1saved, thumb1, size1, map, youtube, rownum as r FROM ( SELECT newsno, memberno, classifyno, title, content, recom, cnt, replycnt, rdate, file1, file1saved, thumb1, size1, map, youtube FROM news
WHERE classifyno=#{classifyno}
WHERE classifyno=#{classifyno} AND (UPPER(title) LIKE '%' || UPPER(#{word}) || '%' OR UPPER(content) LIKE '%' || UPPER(#{word}) || '%' OR UPPER(word) LIKE '%' || UPPER(#{word}) || '%')
ORDER BY newsno DESC ) ) WHERE r >= #{start_num} AND r <= #{end_num}

SELECT COUNT(*) as cnt 
FROM news
WHERE newsno = #{newsno} AND passwd=#{passwd}

mapper.update:
UPDATE news
SET title=#{title}, content=#{content},  word=#{word}
WHERE newsno = #{newsno}

UPDATE news
SET file1=#{file1}, file1saved=#{file1saved}, thumb1=#{thumb1}, size1=#{size1}
WHERE newsno = #{newsno}

mapper.select:
SELECT COUNT(*) as cnt 
FROM news 
WHERE classifyno=#{classifyno}

SELECT COUNT(*) as cnt 
FROM news 
WHERE memberno=#{memberno}

mapper.update:
UPDATE news
SET replycnt = replycnt + 1
WHERE newsno = #{newsno}

UPDATE news
SET replycnt = replycnt - 1
WHERE newsno = #{newsno}
